actions.script:
//use the current pattern to spawn enemies
for(var i = 0; i < currPat.grouping[currPat.gindex]; i++){
	runtime.callFunction("SpawnEnemy", (currPat.angles[currPat.aindex] + currPat.offset) % 359);
	currPat.aindex += 1;
}
currPat.gindex += 1;
//if the pattern has ended, grab a random one that is close to currIntensity
if(currPat.gindex >= currPat.grouping.length){
	currPat.gindex = 0;
	currPat.aindex = 0;
	var indicies = [];	//this will hold where chosen patterns are
	for(var i = 0; i < patterns.length; i++){
		//add every pattern within 15 intensity of currIntensity
		if(Math.abs(patterns[i].intensity - (currIntensity + intensityModifier)) < 15){
			indicies.push(i);
		}
	}
	//increase the intensity relative to last pattern's length
	currIntensity += currPat.grouping.length;
	console.log(currIntensity);	//track intensity through the log
	if(currIntensity > 100){currIntensity = 100;}
	//assign new pattern
	currPat = patterns[indicies[Math.floor(Math.random() * indicies.length)]];
}

for(var i = 0; i < currPat.grouping[currPat.gindex]; i++){
	runtime.callFunction("SpawnEnemy", (currPat.angles[currPat.aindex] + currPat.offset) % 359);
	currPat.aindex += 1;
}
currPat.gindex += 1;

//load a blank pattern so that only one pattern gets played at a time
if(currPat.gindex >= currPat.grouping.length){
	currPat.gindex = 0;
	currPat.aindex = 0;
	currPat = overloadedPatterns[0];
}

let i = Math.floor(Math.random() * (overloadedPatterns.length - 2)) + 1;
currPat = overloadedPatterns[i];
console.log(i);
